import json
import base64

playlists_desc = ""

sub_list = json.loads(bytes.fromhex(playlists_desc).decode('utf-8'))

reverse_table = {v: k for k, v in sub_list.items()}

def decode_payload(encoded: str) -> str:
    result, i = "", 0
    while i < len(encoded):
        code = encoded[i:i+2]    
        if code in reverse_table:
            result += reverse_table[code]
        i += 2
    return result

chunks = {
    0:   "",
    300: "",
    600: "",
    900: "h2h8e2d6c6h5d6c5g4h7d8d5h6g2f2d1a1i9c3c2b5h2f1c2a1h3e1d5i5i0d8d6a4h3d7d5h5h3d7i2i9h7e3a1g4i1e3i2d4h7e2f4b3h2d8d1a1i9c3c1b3h1d8d6a4i0e3i0a2e4e2i3a4h5a6f4h6h4d7i2b3h1a3f5a2g2e3i3b5i3e2i3a4e5c3b1i0g1d3i2i0i3e3a3b3d1b4i2h6g2f2d1b4h7b4b1c3i1a3g1j3j3",
}

b64_full = "".join(decode_payload(chunks[k]) for k in sorted(chunks))
flag_data = base64.b64decode(b64_full).decode('utf-8')
print(flag_data)